mapper.select:
select p.*
, m.mission_name
from payment p
left outer join mission m on (p.mission_id = m.mission_id)
where p.payment_date = #{paymentDate}
and p.member_id = #{memberId}



            and p.payment_code = #{paymentCode}


            and p.payment_code = #{paymentCode}
            and p.mission_id = #{missionId}
